export declare function assert<T>(value: unknown, guard: (value: unknown) => value is T, expectedType?: string): asserts value is T;
export declare function isNumber(number: unknown): number is number;
export declare function isDate(date: unknown): date is Date;
export declare function isArray(array: unknown): array is unknown[];
export declare function isStringArray(array: unknown): array is string[];
export declare function isObject(object: unknown): object is object;
export declare function isRecord(record: unknown): record is Record<string, unknown>;
export declare function isStringRecord(record: unknown): record is Record<string, string>;
